def merge_the_tools(string, k):
    # your code goes here
    for i in range(len(string)//k):
        charsInSub=[]
        for char in (string[(i*k):((i+1)*k)]):
            if char not in charsInSub:
                print(char, end="")
                charsInSub.append(char)
        print('')
        
            
    
if __name__ == '__main__':
    string=''
    k = 1
    merge_the_tools(string, k)